      position: relative;
      width: 100%;

      & input {
        border: none;
        background: c.$pri-link-water-white;
        border-radius: 50px;
        box-shadow: 1px 1px 6px c.$sec-light-san-juan-blue;
        padding: 20px 25px;
        width: 80%;
        font-family: c.$sec-font-family;
        font-size: 15px;
        font-weight: c.$sec-font-bold;
        color: c.$sec-san-juan-blue;

        &:focus {
          outline: none;
        }

        &::placeholder {
          font-family: c.$sec-font-family;
          font-size: 15px;
          font-weight: c.$sec-font-bold;
        }
      }

      & button {
        position: absolute;
        top: 0;
        right: 0;
        @include c.pink-btn-on-light;
        border: none;
        height: 100%;
        padding: 15px 30px;
      }
    }

    & .questions {
      margin-top: 20px;
      padding-left: 30px;
      & a {
        color: c.$sec-san-juan-blue;

        & span {
          font-weight: c.$sec-font-bold;
        }
      }
    }
  }

  &-right {
    position: relative;
    width: 380px;
    z-index: 8;
    & img {
      width: 100%;
    }
  }
}

#patners-container {
  position: relative;
  width: 100%;
  background: c.$sec-mirage-blue;
  margin-top: 70px;
  overflow: hidden;

  & .patners-pattern {
    position: absolute;
    left: -20%;
    top: -90%;
    z-index: 3;
  }
}

.patners {
  position: relative;
  width: 90%;
  max-width: 1400px;
  margin-inline: auto;
  padding: 100px 0;
  @include c.flex(space-between, center, row);
  z-index: 4;

  &-left {
    width: 40%;
    color: c.$pri-link-water-white;

    & .heading {
      font-size: map-get(c.$font-sizes, H2);
    }

    & .copy {
      font-size: map-get(c.$font-sizes, body);
      margin: 30px 0;
      margin-top: 20px;
    }

    & a {
      display: inline-block;
      @include c.btn-on-dark;
    }
  }

  &-right {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: auto;
    grid-column-gap: 30px;
    grid-row-gap: 40px;
    margin-left: 20px;
    align-items: center;

    & img {
      fill: white;
    }
  }
}

#cores-container {
  position: relative;
  margin-top: 70px;
  //   overflow: hidden;
  padding-bottom: 50px;

  & .core-pattern {
    position: absolute;
    right: -20%;
    top: 38%;
    width: 600px;
    height: 600px;
    z-index: 2;
    background-image: url('../../assets/shared/desktop/bg-pattern-circle.svg');
    background-size: cover;

    & img {
      width: 100%;
    }
  }
}

.cores {
  position: relative;
  width: 90%;
  max-width: 1400px;
  margin-inline: auto;
  z-index: 3;

  & .core {
    @include c.flex(space-between, center, row);
    width: 100%;

    &2 {
      @include c.flex(space-between, center, row-reverse);
    }

    &-img {
      width: 50%;

      & img {
        width: 100%;
      }
    }

    &-content {
      width: 50%;
      & .heading {
        font-size: map-get(c.$font-sizes, H2);
        color: c.$sec-san-juan-blue;
        margin-bottom: 15px;
      }

      & .copy {
        color: c.$sec-light-san-juan-blue;
        width: 80%;
      }
    }
  }
}

#values-container {
  margin-top: 20px;
}

.values {
  width: 90%;
  max-width: 1400px;
  margin-inline: auto;
  @include c.flex(space-between, center, row);
}

.value {
  @include c.flex(center, center, column);
  text-align: center;

  &:not(:last-child) {
    margin-right: 25px;
  }

  &-img {
    width: 80px;

    & img {
      width: 100%;
    }
  }

  & .heading {
    font-size: map-get(c.$font-sizes, H3);
    font-weight: c.$sec-font-bold;
    color: c.$sec-san-juan-blue;
    margin: 20px 0 10px;
  }

  & .copy {
    color: c.$sec-light-san-juan-blue;
  }
}

