@import url('https://fonts.googleapis.com/css2?family=DM+Serif+Display&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Public+Sans:wght@400;500;600;700&display=swap');

$main-font-family: 'DM Serif Display', serif;
$sec-font-family: 'Public Sans', sans-serif;

$sec-font-bold: 700;
$sec-font-medium: 500;
$sec-font-regular: 400;

$font-sizes: (
  'H1-large': 72px,
  'H1-small': 56px,
  'H2': 48px,
  'H3-big': 32px,
  'H3': 18px,
  'H4': 24px,
  'body': 15px,
);

$pri-dark-pink: #ba4270;
$pri-link-water-white: #fbfcfe;
$sec-san-juan-blue: #36536b;
$sec-mirage-blue: #1b262f;
$sec-charm-pink: #da6d97;
$sec-light-san-juan-blue: #6c8294;

@mixin flex($jus, $align, $dir) {
  display: flex;
  justify-content: $jus;
  align-items: $align;
  flex-direction: $dir;
}

@mixin transition {
  transition: all 300ms ease-in;
}

@mixin pink-btn-on-light {
  font-family: $sec-font-family;
  font-weight: $sec-font-bold;
  font-size: map-get($font-sizes, body);
  text-decoration: none;
  color: $pri-link-water-white;
  background: $pri-dark-pink;
  padding: 15px 25px;
  border-radius: 50px;
  cursor: pointer;
  @include transition;

  &:hover {
    background: $sec-charm-pink;
  }

  &.disabled {
    opacity: 0.4;
  }
}

@mixin btn-on-dark {
  font-family: $sec-font-family;
  font-weight: $sec-font-bold;
  font-size: map-get($font-sizes, body);
  text-decoration: none;
  color: $pri-link-water-white;
  background: transparent;
  border: 2px solid $pri-link-water-white;
  padding: 15px 25px;
  border-radius: 50px;
  cursor: pointer;
  @include transition;

  &:hover {
    background: $pri-link-water-white;
    color: $sec-san-juan-blue;
  }

  &.disabled {
    opacity: 0.5;
  }
}

@mixin btn-on-light {
  font-family: $sec-font-family;
  font-weight: $sec-font-bold;
  font-size: map-get($font-sizes, body);
  text-decoration: none;
  color: $sec-san-juan-blue;
  background: transparent;
  border: 1.5px solid $sec-san-juan-blue;
  padding: 15px 25px;
  border-radius: 50px;
  cursor: pointer;
  @include transition;

  &:hover {
    background: $sec-san-juan-blue;
    color: $pri-link-water-white;
  }

  &.disabled {
    opacity: 0.5;
  }
}

