    & a {
      white-space: nowrap;
    }
  }

  .cta {
    @include c.flex(center, center, column);

    & .heading {
      font-size: map-get(c.$font-sizes, H2);
      margin-bottom: 30px;
    }

    &-form {
      position: relative;
      width: 90%;
    }
  }
}

@media (max-width: 600px) {
  .header {
    @include c.flex(space-between, center, row);
    overflow-x: hidden;

    &-left {
      &.open {
        z-index: 5;
      }
    }

    &-right {
      position: absolute;
      right: 0;
      top: 0;
      @include c.flex(flex-start, center, column);
      width: 80%;
      height: 100vh;
      padding-top: 150px;
      background: c.$sec-mirage-blue;
      transform: translateX(100%);
      @include c.transition;
      z-index: 7;

      &.open {
        transform: translateX(0);
      }

      &:after {
        position: absolute;
        content: '';
        top: 80px;
        width: 88%;
        height: 1px;
        background: c.$sec-light-san-juan-blue;
        z-index: 10;
      }

      & .pattern.top-pattern {
        width: 680px;
        top: -30%;
        right: 0%;
        transition: all -200ms ease-out;

        &.open {
          transform: translateX(68%);
        }
      }

      & .links {
        position: relative;
        @include c.flex(space-between, center, column);
        margin-left: 0px;
        margin-bottom: 40px;
        z-index: 10;

        & .link {
          position: relative;
          opacity: 0;
          @include c.transition;
          z-index: 10;

          &.open {
            opacity: 1;
          }
          &:not(:last-child) {
            margin-right: 0px;
            margin-bottom: 40px;
          }
          & a {
            position: relative;
            color: c.$pri-link-water-white;
            font-size: 20px;

            &:hover {
              color: c.$pri-link-water-white;
            }

            &.active {
              color: c.$pri-link-water-white;
              &:after {
                content: '';
                position: absolute;
                bottom: -5px;
                left: 0;
                width: 100%;
                height: 2px;
                background: c.$pri-link-water-white;
              }
            }
          }
        }
      }

      & .header-right-btn {
        width: 80%;
        margin-inline: auto;
        opacity: 0;

        @include c.transition;

        &.open {
          opacity: 1;
        }

        & a {
          position: relative;
          display: inline-block;
          text-align: center;
          width: 100%;
          @include c.pink-btn-on-light;

          &:hover {
            background: c.$pri-dark-pink;
          }
        }
      }
    }

    .menu-icon {
      display: block;

      & #menu-close {
        display: none;
        @include c.transition;
      }

      &.open {
        & #menu-open {
          display: none;
        }

        & #menu-close {
          display: block;
        }
      }
    }
  }

  #pricings-container {
    &.open {
      z-index: 5;
    }
    & .heading {
      font-size: map-get(c.$font-sizes, H3-big);
      margin-bottom: 40px;
    }
  }

  .pricings {
    @include c.flex(center, center, column);
  }

  .pricing {
    @include c.flex(center, center, column);
    width: 100%;

    &:not(:last-child) {
      margin-right: 0px;
      margin-bottom: 50px;
    }
  }

  .pricing-top {
    text-align: center;
    width: 100%;
    & .plan {
      font-size: map-get(c.$font-sizes, H4);
      margin-bottom: 90px;
    }

    & .copy {
      display: none;
    }

    & .price {
      font-size: map-get(c.$font-sizes, H2);
    }
  }

  .pricing-bottom {
    @include c.flex(center, center, column);

    & ul {
      & li {
        font-family: c.$sec-font-family;
        font-size: 16px;
        color: c.$sec-san-juan-blue;

        &.greyed {
          color: c.$sec-light-san-juan-blue;
          padding-left: 30px;

          & img {
            display: none;
          }
        }

        &:not(:last-child) {
          margin-bottom: 20px;
        }

        & img {
          margin-right: 10px;
        }
      }
    }
  }

  .pricing-btn {
    margin-top: 20px;
    & a {
      white-space: nowrap;
    }
  }

  footer {
    & .footer-bg {
      right: -16%;
      top: 30%;
    }
  }

  .footer {
    @include c.flex(center, center, column);
  }

  .footer-left {
    @include c.flex(center, center, column);

    & .footer-logo {
      width: 130px;

      & img {
        width: 100%;
      }
    }

    & .footer-links {
      margin-left: 0px;
      margin: 30px 0;
      text-align: center;

      & span {
        display: block;

        &:not(:last-child) {
          margin-right: 0px;
          margin-bottom: 30px;
        }
      }
    }
  }
}

@media (max-width: 450px) {
  .header {
    &-right {
      & .pattern.top-pattern {
        width: 680px;
        top: -62%;
        right: 0%;

        &.open {
