  }

  .patners {
    padding: 80px 0;
    @include c.flex(space-between, center, column-reverse);

    &-left {
      width: 100%;
      text-align: center;

      & .heading {
        font-size: map-get(c.$font-sizes, H2);
      }

      & .copy {
        font-size: map-get(c.$font-sizes, body);
        width: 80%;
        margin-inline: auto;
      }

      & a {
        display: inline-block;
        @include c.btn-on-dark;
      }
    }

    &-right {
      display: grid;
      grid-template-columns: repeat(3, 1fr);
      grid-template-rows: auto;
      grid-column-gap: 30px;
      grid-row-gap: 40px;
      margin-left: 0px;
      margin-bottom: 30px;

      & img {
        fill: white;
      }
    }
  }
}

@media (max-width: 600px) {
  .header {
    @include c.flex(space-between, center, row);
    width: 90%;
    max-width: 1400px;
    margin-inline: auto;
    padding: 30px 0;

    &-left {
    }

    &-right {
      & .right-container {
        position: absolute;
        right: 0;
        top: 0;
        @include c.flex(flex-start, center, column);
        width: 80%;
        height: 100vh;
        padding-top: 150px;
        background: c.$sec-mirage-blue;
        transform: translateX(100%);
        @include c.transition;
        z-index: 7;

        &.open {
          transform: translateX(0);
        }

        &:after {
          position: absolute;
          content: '';
          top: 80px;
          width: 80%;
          height: 1px;
          background: c.$sec-light-san-juan-blue;
          z-index: 10;
        }

        & .pattern.top-pattern {
          width: 680px;
          top: -20%;
          right: 90%;

          &.open {
            transform: translateX(70%);
          }
        }
      }

      & .links {
        position: relative;
        @include c.flex(space-between, center, column);
        margin-left: 0px;
        margin-bottom: 40px;
        z-index: 10;

        & .link {
          position: relative;
          opacity: 0;
          @include c.transition;
          z-index: 10;

          &.open {
            opacity: 1;
          }
          &:not(:last-child) {
            margin-right: 0px;
            margin-bottom: 40px;
          }
          & a {
            color: c.$pri-link-water-white;
            font-size: 20px;

            &:hover {
              color: c.$pri-link-water-white;
            }
          }
        }
      }

      & .header-right-btn {
        width: 80%;
        margin-inline: auto;
        opacity: 0;

        @include c.transition;

        &.open {
          opacity: 1;
        }

        & a {
          position: relative;
          display: inline-block;
          text-align: center;
          width: 100%;
          @include c.pink-btn-on-light;

          &:hover {
            background: c.$pri-dark-pink;
          }
        }
      }
    }

    .menu-icon {
      display: block;

      & #menu-close {
        display: none;
        @include c.transition;
      }

      &.open {
        & #menu-open {
          display: none;
        }

        & #menu-close {
          display: block;
        }
      }
    }
  }

  #main-hero-container {
    &.open {
      z-index: 2;
    }
  }

  .main-hero {
    @include c.flex(space-between, center, column-reverse);

    &-left {
      width: 100%;
      text-align: center;
      & .heading {
        font-size: map-get(c.$font-sizes, H3-big);
        margin-bottom: 50px;
      }

      & .hero-form {
        width: 100%;
        margin-inline: auto;

        & input {
          width: 100%;
          margin-bottom: 20px;
        }

        & button {
          position: relative;
          display: block;
          width: 100%;
          padding: 20px 30px;
        }
      }

      & .questions {
        padding-left: 0px;
      }
    }

    &-right {
      width: 380px;

      &.open {
        z-index: 5;
      }
      & img {
        width: 100%;
      }
    }
  }

  #patners-container {
    & .patners-pattern {
      position: absolute;
      left: -20%;
      top: -90%;
    }
  }

  .patners {
    padding: 80px 0;
    @include c.flex(space-between, center, column-reverse);

    &-left {
      width: 100%;
      text-align: center;

      & .heading {
        font-size: map-get(c.$font-sizes, H3-big);
      }

      & .copy {
        width: 100%;
      }

      & a {
        display: inline-block;
        @include c.btn-on-dark;
      }
    }

    &-right {
      display: grid;
      grid-template-columns: repeat(2, 1fr);
      grid-template-rows: auto;
      grid-column-gap: 30px;
      grid-row-gap: 40px;
      margin-left: 0px;
      margin-bottom: 30px;

      & img {
        fill: white;
      }
    }
  }
}

@media (max-width: 420px) {
  .header {
    &-right {
      & .right-container {
        & .pattern.top-pattern {
          width: 500px;
          top: -15%;
          right: 80%;

          &.open {
            transform: translateX(60%);
          }
        }
      }
    }
  }

  #patners-container {
    & .patners-pattern {
      position: absolute;
      left: -200px;
      top: -580px;
    }
  }
}

@media (max-width: 360px) {
  #patners-container {
    & .patners-pattern {
      position: absolute;
      left: -250px;
      top: -580px;
    }
  }
  .patners {
    &-right {
      @include c.flex(center, center, column);
      margin-left: 0px;
      margin-bottom: 30px;

      & img {
        fill: white;
      }
    }
  }
}
